function ocultaElementosDoHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exbeElementosDoHeader(){document.querySelector("header").classList.remove("header--is-hidden")}function abreOuFechaResposta(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function removeBotaoAtivo(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}function escondeTodasAsAbas(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),o=document.querySelectorAll("[data-faq-question]");let e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?ocultaElementosDoHeader:exbeElementosDoHeader)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(){var e=this.dataset.tabButton,e=document.querySelector(`[data-tab-id="${e}"]`);escondeTodasAsAbas(),e.classList.add("shows__list--is-active"),removeBotaoAtivo(),this.classList.add("shows__tabs__button--is-active")});for(let e=0;e<o.length;e++)o[e].addEventListener("click",abreOuFechaResposta)});